// Extract text from the active branch (Agent or Quick Reply)
const json = $input.first().json;
let text = '';

// Try to get text from input first
if (json.output) {
    text = json.output; // From Sales Agent
} else if (json.quick_reply) {
    text = json.quick_reply; // From Quick Reply
} else {
    // Fallback: try to resolve from node references safely
    try { text = $('ü§ñ Sales Agent').first().json.output; } catch(e) {}
    if (!text) try { text = $('üí¨ Respuesta R√°pida').first().json.quick_reply; } catch(e) {}
}

text = text || '';

// Extract [FOTO:apartment_id] tags
const photoMatches = text.match(/\[FOTO:(\w+)\]/gi) || [];
const photo_apartments = photoMatches.map(m => m.match(/FOTO:(\w+)/i)[1]);

// Clean text: remove tags
const clean_text = text.replace(/\[FOTO:\w+\]/gi, '').replace(/\[VIDEO:\w+\]/gi, '').trim();

// Get context from Router (always available)
const router = $('üß≠ Router Inteligente').first().json;

return [{
    json: {
        clean_text,
        has_photos: photo_apartments.length > 0,
        photo_apartments,
        account_id: router.account_id,
        conversation_id: router.conversation_id,
        sender_name: router.sender_name
    }
}];

// Build photo fetch items from apartment IDs
const apts = $('üñºÔ∏è Procesar Fotos').item.json.photo_apartments;
const account_id = $('üñºÔ∏è Procesar Fotos').item.json.account_id;
const conversation_id = $('üñºÔ∏è Procesar Fotos').item.json.conversation_id;

const items = apts.map(apt => ({
    json: {
        apt_id: apt,
        api_url: "https://availability-api.parallext.cloud/apartments/" + apt + "/photos",
        account_id: account_id,
        conversation_id: conversation_id
    }
}));
return items;

// üß≠ Router Inteligente - bypasses LLM for trivial messages
const msg = $('üìã Extraer Datos').item.json.message_content.trim();
const name = $('üìã Extraer Datos').item.json.sender_name;
const lower = msg.toLowerCase().replace(/[!¬°¬ø?.,;:]/g, '').trim();

// Only match if the ENTIRE message is a greeting or ack (no compound messages)
const GREETINGS = /^(hola|hi|hello|hey|buenas?|buenos?\s*(dias|tardes|noches)|que\s*tal|saludos?)$/i;
const ACKS = /^(ok|okay|listo|vale|perfecto|genial|gracias|muchas\s*gracias|de\s*acuerdo|entendido|claro|super|excelente|buenisimo|chevere)$/i;

let useAgent = true;
let quickReply = '';

if (GREETINGS.test(lower)) {
    const isPhoneNumber = name && /^\+?\d+$/.test(name.trim());
    const hasName = name && name.trim() && !isPhoneNumber;
    const g = hasName ? `¬°Hola, ${name}! üëã` : '¬°Hola! üëã ¬øCon qui√©n tengo el gusto?';
    quickReply = hasName 
        ? `${g} Soy el asistente de Amazon Minimalist. ¬øEn qu√© puedo ayudarte? üòä`
        : g;
    useAgent = false;
} else if (ACKS.test(lower)) {
    quickReply = '¬°Con gusto! Si necesitas algo m√°s, aqu√≠ estoy üòä';
    useAgent = false;
}

return [{
    json: {
        useAgent,
        quickReply,
        message_content: msg,
        account_id: $('üìã Extraer Datos').item.json.account_id,
        conversation_id: $('üìã Extraer Datos').item.json.conversation_id,
        sender_name: name,
        sender_phone: $('üìã Extraer Datos').item.json.sender_phone,
        sender_email: $('üìã Extraer Datos').item.json.sender_email
    }
}];

// Expand photo URLs into individual items (send up to 8)
const response = $input.first().json;
const photos = response.photos || [];

const account_id = $('üñºÔ∏è Procesar Fotos').item.json.account_id;
const conversation_id = $('üñºÔ∏è Procesar Fotos').item.json.conversation_id;

// Send up to 8 photos
const selected = photos.slice(0, 8);

return selected.map(url => ({
    json: {
        photo_url: url,
        account_id: account_id,
        conversation_id: conversation_id
    }
}));
